import React, { useState } from "react";
import DeviceCard from "./DeviceCard";

function Dashboard() {
  // Khởi tạo state devices an toàn với mảng rỗng
  const [devices, setDevices] = useState([
    {
      id: 1,
      device_id: "D001",
      user_id: "U001",
      name: "Máy lạnh",
      kWh: 12,
      time: "10:00 - 12:00",
      address: "Phòng khách",
      status: "On",
    },
    {
      id: 2,
      device_id: "D002",
      user_id: "U002",
      name: "Tivi",
      kWh: 5,
      time: "18:00 - 20:00",
      address: "Phòng ngủ",
      status: "Off",
    },
  ]);

  // Filter On devices (ví dụ)
  const onDevices = devices.filter((d) => d.status.toLowerCase() === "on");
  const offDevices = devices.filter((d) => d.status.toLowerCase() === "off");

  return (
    <div style={{ padding: 20 }}>
      {" "}
      <h2>Dashboard</h2>
      ```
      <h3>Thiết bị đang ON</h3>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {onDevices.length > 0 ? (
          onDevices.map((d) => (
            <DeviceCard
              key={d.id}
              device={d}
              onEdit={() => console.log("Edit", d.id)}
              onDelete={() =>
                setDevices(devices.filter((dev) => dev.id !== d.id))
              }
            />
          ))
        ) : (
          <p>Không có thiết bị nào đang bật</p>
        )}
      </div>
      <h3>Thiết bị đang OFF</h3>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {offDevices.length > 0 ? (
          offDevices.map((d) => (
            <DeviceCard
              key={d.id}
              device={d}
              onEdit={() => console.log("Edit", d.id)}
              onDelete={() =>
                setDevices(devices.filter((dev) => dev.id !== d.id))
              }
            />
          ))
        ) : (
          <p>Không có thiết bị nào đang tắt</p>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
