import React from 'react';

function DeviceCard({ device, onDelete }) {
  return (
    <div style={{ padding: 15, background:'#fff', borderRadius:8, boxShadow:'0 0 5px rgba(0,0,0,0.1)', position:'relative' }}>
      <h4>{device.name}</h4>
      <p>Điện tiêu thụ: {device.kWh} kWh</p>
      <p>Thời gian sử dụng: {device.time}</p>
      <button 
        onClick={onDelete} 
        style={{ position:'absolute', top:5, right:5, display:'inline-block' }}
      >
        ❌
      </button>
    </div>
  );
}

export default DeviceCard;
