import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Tooltip, Legend } from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, LineElement, PointElement, ArcElement, Tooltip, Legend);

function Dashboard({ devices }) {
  const totalPower = devices.reduce((sum, d) => sum + (d.kWh || 0), 0);
  const labels = devices.map(d => d.DEVICE_NAME);

  const chartData = {
    labels,
    datasets: [{
      label: "kWh",
      data: devices.map(d => d.kWh || 0),
      backgroundColor: devices.map(d => d.color),
      borderColor: devices.map(d => d.color),
      borderWidth: 2,
    }],
  };

  const pieData = {
    labels,
    datasets: [{
      data: devices.map(d => d.kWh || 0),
      backgroundColor: devices.map(d => d.color),
      borderColor: devices.map(d => d.color),
    }],
  };

  const options = { responsive: true, plugins: { legend: { position: "top" } } };

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "20px", marginTop: "20px" }}>
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ cột</h3>
        <Bar data={chartData} options={options} />
      </div>
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ đường</h3>
        <Line data={chartData} options={options} />
      </div>
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ tròn</h3>
        <Pie data={pieData} options={options} />
      </div>
      <div style={{ flex: "1 1 100%", padding: "16px", border: "1px solid #ccc", borderRadius: "8px" }}>
        <p><strong>Tổng kWh:</strong> {totalPower}</p>
        <p><strong>Số thiết bị:</strong> {devices.length}</p>
      </div>
    </div>
  );
}

export default Dashboard;
