import React, { useState } from "react";
import DeviceCard from "./DeviceCard";
import Dashboard from "./Dashboard";

function Device() {
const [devices, setDevices] = useState([
{ id: 1, name: "Máy lạnh", kWh: 12, time: "10:00 - 12:00", date: "2025-01-15" },
{ id: 2, name: "Tivi", kWh: 5, time: "18:00 - 20:00", date: "2025-02-20" },
]);

// Thêm thiết bị
const [newName, setNewName] = useState("");
const [newKwh, setNewKwh] = useState("");
const [newTime, setNewTime] = useState("");
const [newDate, setNewDate] = useState("");

// Edit state
const [editing, setEditing] = useState(null);
const [editName, setEditName] = useState("");
const [editKwh, setEditKwh] = useState("");
const [editTime, setEditTime] = useState("");
const [editDate, setEditDate] = useState("");

const addDevice = () => {
if (!newName || !newKwh || !newTime || !newDate) {
alert("Nhập đầy đủ thông tin!");
return;
}
setDevices([...devices, { id: Date.now(), name: newName, kWh: Number(newKwh), time: newTime, date: newDate }]);
setNewName(""); setNewKwh(""); setNewTime(""); setNewDate("");
};

const openEdit = (device) => {
setEditing(device.id);
setEditName(device.name);
setEditKwh(device.kWh);
setEditTime(device.time);
setEditDate(device.date);
};

const saveEdit = () => {
setDevices(devices.map(d => d.id === editing ? { ...d, name: editName, kWh: Number(editKwh), time: editTime, date: editDate } : d));
setEditing(null);
};

const deleteDevice = (id) => setDevices(devices.filter(d => d.id !== id));

return ( <div className="p-6"> <h2 className="text-3xl font-bold mb-6">Device Manager</h2>

```
  {/* Form thêm thiết bị */}
  <div className="flex gap-2 mb-6">
    <input type="text" placeholder="Tên thiết bị" value={newName} onChange={e => setNewName(e.target.value)} className="p-2 border rounded"/>
    <input type="number" placeholder="kWh" value={newKwh} onChange={e => setNewKwh(e.target.value)} className="p-2 border rounded w-20"/>
    <input type="text" placeholder="Thời gian" value={newTime} onChange={e => setNewTime(e.target.value)} className="p-2 border rounded w-36"/>
    <input type="date" value={newDate} onChange={e => setNewDate(e.target.value)} className="p-2 border rounded"/>
    <button onClick={addDevice} className="bg-green-500 text-white px-3 rounded">+ Add</button>
  </div>

  {/* Danh sách thiết bị */}
  <div className="grid grid-cols-2 gap-4 mb-8">
    {devices.map(d => (
      <DeviceCard key={d.id} device={d} onEdit={() => openEdit(d)} onDelete={() => deleteDevice(d.id)} />
    ))}
  </div>

  {/* Modal edit */}
  {editing && (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
      <div className="bg-white p-6 rounded-xl w-96">
        <h3 className="text-xl mb-4">Sửa thiết bị</h3>
        <input type="text" value={editName} onChange={e => setEditName(e.target.value)} className="p-2 border rounded w-full mb-2"/>
        <input type="number" value={editKwh} onChange={e => setEditKwh(e.target.value)} className="p-2 border rounded w-full mb-2"/>
        <input type="text" value={editTime} onChange={e => setEditTime(e.target.value)} className="p-2 border rounded w-full mb-2"/>
        <input type="date" value={editDate} onChange={e => setEditDate(e.target.value)} className="p-2 border rounded w-full mb-4"/>
        <div className="flex justify-end gap-2">
          <button onClick={saveEdit} className="bg-blue-500 text-white px-3 rounded">Lưu</button>
          <button onClick={() => setEditing(null)} className="bg-gray-400 px-3 rounded">Hủy</button>
        </div>
      </div>
    </div>
  )}

  {/* Dashboard */}
  <Dashboard devices={devices} />
</div>


);
}

export default Device;
