import React, { useState } from 'react';
import DeviceCard from './DeviceCard';

function Device() {
  const [devices, setDevices] = useState([
    { id:1, name:'Máy lạnh', kWh: 12, time:'10:00 - 12:00' },
    { id:2, name:'Tivi', kWh: 5, time:'18:00 - 20:00' }
  ]);

  const addDevice = () => {
    const newDevice = { id: Date.now(), name:'Thiết bị mới', kWh:0, time:'00:00 - 00:00' };
    setDevices([...devices, newDevice]);
  };

  const deleteDevice = (id) => {
    setDevices(devices.filter(d => d.id !== id));
  };

  return (
    <div>
      <h2>Device</h2>
      <button onClick={addDevice} style={{ marginBottom: 15 }}>+ Add Device</button>
      <div className="device-grid">
        {devices.map(d => (
          <DeviceCard key={d.id} device={d} onDelete={()=>deleteDevice(d.id)} />
        ))}
      </div>
    </div>
  );
}

export default Device;
