import React, { useState, useEffect } from "react";
import axios from "axios";
import DeviceCard from "./DeviceCard";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, devices, setDevices }) {
const [form, setForm] = useState({
DEVICE_NAME: "",
ADDRESS: addresses[0],
STATUS: "On",
});

// Load devices từ server
const fetchDevices = async () => {
try {
const res = await axios.get(
`http://localhost:5000/api/devices/user/${user.USER_ID}`
);
setDevices(res.data);
} catch (err) {
console.error("Error fetching devices:", err);
}
};

useEffect(() => {
if (user) fetchDevices();
}, [user]);

// Thêm device mới
const handleAdd = async () => {
if (!form.DEVICE_NAME || !form.ADDRESS || !form.STATUS)
return alert("Nhập đủ thông tin thiết bị");

``
try {
  const res = await axios.post("http://localhost:5000/api/devices", {
    USER_ID: user.USER_ID,
    DEVICE_NAME: form.DEVICE_NAME,
    ADDRESS: form.ADDRESS,
    STATUS: form.STATUS,
  });

  setDevices([...devices, res.data]);
  setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
} catch (err) {
  console.error("Error saving device:", err);
  alert("Lưu thiết bị thất bại!");
}


};

// Xóa device
const handleDelete = async (deviceId) => {
try {
await axios.delete(`http://localhost:5000/api/devices/${deviceId}`);
setDevices(devices.filter((d) => d.DEVICE_ID !== deviceId));
} catch (err) {
console.error("Error deleting device:", err);
alert("Xóa thiết bị thất bại!");
}
};

return (
<div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
{/* Form thêm thiết bị */}
<div
style={{
padding: "16px",
border: "1px solid #ccc",
borderRadius: "8px",
maxWidth: "400px",
}}
> <h4>Thêm thiết bị mới</h4>
<input
type="text"
placeholder="Tên thiết bị"
value={form.DEVICE_NAME}
onChange={(e) =>
setForm({ ...form, DEVICE_NAME: e.target.value })
}
style={{ width: "100%", marginBottom: "8px" }}
/>
<select
value={form.ADDRESS}
onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
style={{ width: "100%", marginBottom: "8px" }}
>
{addresses.map((a) => ( <option key={a} value={a}>
{a} </option>
))} </select>
<select
value={form.STATUS}
onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
style={{ width: "100%", marginBottom: "8px" }}
> <option>On</option> <option>Off</option> </select>
<button onClick={handleAdd} style={{ width: "100%" }}>
Thêm thiết bị </button> </div>


  {/* Danh sách device */}
  <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
    {devices.map((d) => (
      <DeviceCard
        key={d.DEVICE_ID}
        device={{
          device_id: d.DEVICE_ID,
          user_id: d.USER_ID,
          name: d.DEVICE_NAME,
          kWh: d.kWh,
          time: d.CREATE_AT,
          address: d.ADDRESS,
          status: d.STATUS,
        }}
        onDelete={() => handleDelete(d.DEVICE_ID)}
        onEdit={() => alert("Chức năng sửa sẽ thêm sau")}
      />
    ))}
  </div>
</div>


);
}

export default Device;
