import React, { useState } from "react";
import Device from "./Device";
import Dashboard from "./Dashboard";

function App() {
  const [devices, setDevices] = useState([]);
  
  // Khi Device thay đổi (thêm mới), cập nhật dashboard
  const handleDevicesUpdate = (updatedDevices) => {
    setDevices(updatedDevices);
  };

  const dummyUser = { USER_ID: 1 }; // thay bằng user thật nếu có login

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>Quản lý thiết bị & Dashboard năng lượng</h2>
      <Device user={dummyUser} onDevicesUpdate={handleDevicesUpdate} />
      <Dashboard devices={devices} />
    </div>
  );
}

export default App;
