import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function Charts({ dailyUsage, deviceUsage }) {
  const labels = dailyUsage.map((d) => d.date);
  const barLineData = {
    labels,
    datasets: [
      {
        type: "bar",
        label: "Consumption (kWh)",
        data: dailyUsage.map((d) => d.kWh),
        backgroundColor: "rgba(75,192,192,0.6)",
      },
      {
        type: "line",
        label: "Trend",
        data: dailyUsage.map((d) => d.kWh),
        borderColor: "rgba(255,99,132,1)",
        fill: false,
      },
    ],
  };

  const pieData = {
    labels: deviceUsage.map((d) => d.name),
    datasets: [
      {
        data: deviceUsage.map((d) => d.kWh),
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0"],
      },
    ],
  };

  return (
    <div style={{ display: "flex", gap: 20, flexWrap: "wrap" }}>
      <div style={{ flex: 1, minWidth: 300 }}>
        <Bar data={barLineData} />
      </div>
      <div style={{ flex: 1, minWidth: 300 }}>
        <Line data={barLineData} />
      </div>
      <div style={{ flex: 1, minWidth: 300 }}>
        <Pie data={pieData} />
      </div>
    </div>
  );
}

export default Charts;
