import React from "react";
import { NavLink } from "react-router-dom";

function Sidebar() {
  const menus = [
    { name: "Dashboard", path: "/dashboard" },
    { name: "Device", path: "/device" },
    { name: "Setting", path: "/setting" },
    { name: "Account", path: "/account" },
    { name: "Power Log", path: "/power_log" },
  ];

  return (
    <div
      style={{
        width: 200,
        background: "#1f2937",
        color: "#fff",
        minHeight: "100vh",
        padding: 20,
      }}
    >
      <h2>Energy App</h2>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {menus.map((menu) => (
          <li key={menu.name} style={{ margin: "10px 0" }}>
            <NavLink
              to={menu.path}
              style={({ isActive }) => ({
                padding: 10,
                display: "block",
                color: isActive ? "#fff" : "#9ca3af",
                backgroundColor: isActive ? "#374151" : "transparent",
                borderRadius: 4,
                textDecoration: "none",
              })}
            >
              {menu.name}
            </NavLink>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sidebar;
