import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Login from "./components/Login";
import "./App.css";

function App() {
const [user, setUser] = useState(null);
const [activeMenu, setActiveMenu] = useState("Dashboard");
const [devices, setDevices] = useState([]); // đây là function setDevices

// Logout
const handleLogout = () => {
setUser(null);
setActiveMenu("Dashboard");
setDevices([]);
};

// Render content theo menu
let content;
switch (activeMenu) {
case "Dashboard":
content = <Dashboard devices={devices || []} />;
break;
case "Device":
content = (
<Device
user={user}
devices={devices || []}
setDevices={setDevices} // <--- chắc chắn là function
/>
);
break;
default:
content = <Dashboard devices={devices || []} />;
}

// Nếu chưa login thì hiện login
if (!user) {
return <Login onLogin={setUser} />;
}

return ( <div className="app-container"> <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} /> <div className="main-content"> <Header user={user} onLogout={handleLogout} />
{content} </div> </div>
);
}

export default App;
