import React, { useState, useEffect } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

function Dashboard({ devices = [] }) {
  const [visibleDevices, setVisibleDevices] = useState(
    devices.reduce((acc, d) => ({ ...acc, [d.DEVICE_ID]: true }), {})
  );

  // State thời gian filter
  const [timeRange, setTimeRange] = useState("day"); // "day" | "week" | "month"
  const [filteredDevices, setFilteredDevices] = useState(devices);

  const handleToggleDevice = (id) => {
    setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));
  };

  const handleTimeChange = (e) => {
    setTimeRange(e.target.value);
  };

  // Lọc thiết bị theo toggle và thời gian
  useEffect(() => {
    const now = new Date();
    const filtered = devices.filter((d) => {
      if (!visibleDevices[d.DEVICE_ID]) return false;

      const deviceDate = new Date(d.timestamp || now); // giả sử mỗi device có timestamp
      if (timeRange === "day") {
        return deviceDate.toDateString() === now.toDateString();
      } else if (timeRange === "week") {
        const weekStart = new Date(now);
        weekStart.setDate(now.getDate() - now.getDay());
        return deviceDate >= weekStart && deviceDate <= now;
      } else if (timeRange === "month") {
        return (
          deviceDate.getMonth() === now.getMonth() &&
          deviceDate.getFullYear() === now.getFullYear()
        );
      }
      return true;
    });
    setFilteredDevices(filtered);
  }, [devices, visibleDevices, timeRange]);

  const totalKwh = filteredDevices.reduce(
    (sum, d) => sum + Number(d.kWh || 0),
    0
  );
  const chartLabels = filteredDevices.map((d) => d.DEVICE_NAME);

  // const chartData = {
  //   labels: chartLabels,
  //   datasets: [
  //     {
  //       data: filteredDevices.map((d) => Number(d.kWh || 0)),
  //       backgroundColor: filteredDevices.map((d) => d.color || "#af4c8bff"),
  //       borderColor: "#333",
  //       borderWidth: 2,
  //       tension: 0.4,
  //     },
  //   ],
  // };
  const barData = {
    labels: chartLabels,
    datasets: [
      {
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
        borderColor: "#fff",
        borderWidth: 4,
      },
    ],
  };
  const lineData = {
    labels: chartLabels,
    datasets: [
      {
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
        borderColor: "#fff",
        borderWidth: 4,
      },
    ],
  };
  const pieData = {
    labels: chartLabels,
    datasets: [
      {
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
        borderColor: "#fff",
        borderWidth: 4,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: function (context) {
            const d = filteredDevices[context.dataIndex];
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${
              d.STATUS || ""
            }`;
          },
        },
      },
      legend: { position: "top" },
    },
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "24px",
        padding: "16px",
      }}
    >
      {/* Filter theo thời gian */}
      <div style={{ marginBottom: "12px" }}>
        <label style={{ marginRight: "12px" }}>
          Time Range:
          <select
            value={timeRange}
            onChange={handleTimeChange}
            style={{ marginLeft: "8px" }}
          >
            {" "}
            <option value="day">Hôm nay</option>{" "}
            <option value="week">Tuần này</option>{" "}
            <option value="month">Tháng này</option>{" "}
          </select>{" "}
        </label>{" "}
      </div>

      {/* Toggle thiết bị */}
      <div style={{ marginBottom: "12px" }}>
        {devices.map((d) => (
          <label key={d.DEVICE_ID} style={{ marginRight: "12px" }}>
            <input
              type="checkbox"
              checked={visibleDevices[d.DEVICE_ID]}
              onChange={() => handleToggleDevice(d.DEVICE_ID)}
            />
            {d.DEVICE_NAME}{" "}
          </label>
        ))}
      </div>

      {/* Tổng năng lượng */}
      <div
        style={{
          backgroundColor: "#fff",
          borderRadius: "12px",
          padding: "16px",
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          width: "fit-content",
        }}
      >
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: "24px", fontWeight: "bold", margin: 0 }}>
          {totalKwh} kWh
        </p>
      </div>

      {/* Biểu đồ */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "24px" }}>
        <div
          style={{
            flex: "1 1 400px",
            height: "300px",
            backgroundColor: "#fff",
            borderRadius: "12px",
            padding: "16px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ cột</h4>
          <Bar data={barData} options={options} />
        </div>

        <div
          style={{
            flex: "1 1 400px",
            height: "300px",
            backgroundColor: "#fff",
            borderRadius: "12px",
            padding: "16px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ đường</h4>
          <Line data={lineDatatD} options={options} />
        </div>

        <div
          style={{
            flex: "1 1 300px",
            height: "300px",
            backgroundColor: "#fff",
            borderRadius: "12px",
            padding: "16px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <h4>Biểu đồ tròn</h4>
          <div style={{ width: "80%", height: "80%" }}>
            <Pie
              data={pieData}
              options={{ ...options, maintainAspectRatio: true }}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
