import React, { useState } from "react";

function Device({ devices, setDevices }) {
  const [newName, setNewName] = useState("");
  const [newKwh, setNewKwh] = useState("");
  const [newTime, setNewTime] = useState("");
  const [newStatus, setNewStatus] = useState("On");
  const [newAddress, setNewAddress] = useState("");

  const [editing, setEditing] = useState(null);
  const [editName, setEditName] = useState("");
  const [editKwh, setEditKwh] = useState("");
  const [editTime, setEditTime] = useState("");
  const [editStatus, setEditStatus] = useState("On");
  const [editAddress, setEditAddress] = useState("");

  const addDevice = () => {
    if (!newName || !newKwh || !newTime || !newAddress) {
      alert("Nhập đầy đủ thông tin!");
      return;
    }
    const newDevice = {
      id: Date.now(),
      device_id: "D" + Date.now(),
      user_id: "U001",
      name: newName,
      kWh: Number(newKwh),
      time: newTime,
      status: newStatus,
      address: newAddress,
      date: new Date().toISOString().split("T")[0],
    };
    setDevices([...devices, newDevice]);
    setNewName("");
    setNewKwh("");
    setNewTime("");
    setNewStatus("On");
    setNewAddress("");
  };

  const openEdit = (device) => {
    setEditing(device.id);
    setEditName(device.name);
    setEditKwh(device.kWh);
    setEditTime(device.time);
    setEditStatus(device.status);
    setEditAddress(device.address);
  };

  const saveEdit = () => {
    const updated = devices.map((d) =>
      d.id === editing
        ? {
            ...d,
            name: editName,
            kWh: Number(editKwh),
            time: editTime,
            status: editStatus,
            address: editAddress,
          }
        : d
    );
    setDevices(updated);
    setEditing(null);
  };

  const deleteDevice = (id) => {
    setDevices(devices.filter((d) => d.id !== id));
  };

  return (
    <div className="mb-6">
      <h2 className="text-2xl font-bold mb-3">Device</h2>

      {/* Form thêm thiết bị */}
      <div className="flex flex-wrap gap-2 mb-4">
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="p-2 border rounded"
        />
        <input
          type="number"
          placeholder="kWh"
          value={newKwh}
          onChange={(e) => setNewKwh(e.target.value)}
          className="p-2 border rounded w-20"
        />
        <input
          type="text"
          placeholder="Thời gian"
          value={newTime}
          onChange={(e) => setNewTime(e.target.value)}
          className="p-2 border rounded w-40"
        />
        <input
          type="text"
          placeholder="Địa chỉ"
          value={newAddress}
          onChange={(e) => setNewAddress(e.target.value)}
          className="p-2 border rounded w-40"
        />
        <select
          value={newStatus}
          onChange={(e) => setNewStatus(e.target.value)}
          className="p-2 border rounded"
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button
          onClick={addDevice}
          className="bg-blue-500 text-white px-3 py-2 rounded"
        >
          + Add
        </button>
      </div>

      {/* Danh sách device */}
      <div className="grid md:grid-cols-3 sm:grid-cols-1 gap-3">
        {devices.map((d) => (
          <div
            key={d.id}
            className={`p-4 rounded shadow ${
              d.status === "On" ? "bg-green-100" : "bg-red-100"
            }`}
          >
            <h4 className="font-semibold">{d.name}</h4>
            <p>
              {d.kWh} kWh | {d.time}
            </p>
            <p>{d.address}</p>
            <div className="mt-2 flex gap-2">
              <button
                onClick={() => openEdit(d)}
                className="bg-yellow-400 px-2 py-1 rounded"
              >
                ✏️
              </button>
              <button
                onClick={() => deleteDevice(d.id)}
                className="bg-red-500 text-white px-2 py-1 rounded"
              >
                ❌
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal edit */}
      {editing && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-5 rounded w-96">
            <h3 className="font-bold mb-2">Sửa thiết bị</h3>
            <input
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              className="p-2 border rounded w-full mb-2"
            />
            <input
              value={editKwh}
              onChange={(e) => setEditKwh(e.target.value)}
              className="p-2 border rounded w-full mb-2"
            />
            <input
              value={editTime}
              onChange={(e) => setEditTime(e.target.value)}
              className="p-2 border rounded w-full mb-2"
            />
            <input
              value={editAddress}
              onChange={(e) => setEditAddress(e.target.value)}
              className="p-2 border rounded w-full mb-2"
            />
            <select
              value={editStatus}
              onChange={(e) => setEditStatus(e.target.value)}
              className="p-2 border rounded w-full mb-2"
            >
              <option>On</option>
              <option>Off</option>
            </select>
            <div className="flex justify-end gap-2">
              <button
                onClick={saveEdit}
                className="bg-blue-500 text-white px-3 py-1 rounded"
              >
                Lưu
              </button>
              <button
                onClick={() => setEditing(null)}
                className="bg-gray-400 px-3 py-1 rounded"
              >
                Hủy
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Device;
