import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

// Đăng ký các thành phần chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

function Dashboard({ devices = [] }) {
  const totalKwh = devices.reduce((sum, d) => sum + Number(d.kWh || 0), 0);
  const chartLabels = devices.map((d) => d.DEVICE_NAME);

  // Dữ liệu cột & đường
  const chartData = {
    labels: chartLabels,
    datasets: [
      {
        label: "kWh",
        data: devices.map((d) => Number(d.kWh || 0)),
        backgroundColor: devices.map(() => "#4caf50"),
        borderColor: devices.map(() => "#388e3c"),
        borderWidth: 1,
      },
    ],
  };

  // Dữ liệu pie chart
  const pieData = {
    labels: chartLabels,
    datasets: [
      {
        data: devices.map((d) => Number(d.kWh || 0)),
        backgroundColor: devices.map(
          () => "#" + Math.floor(Math.random() * 16777215).toString(16)
        ),
        borderColor: "#fff",
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { position: "top" },
      tooltip: {
        callbacks: {
          label: function (context) {
            const d = devices[context.dataIndex];
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ID: ${d.DEVICE_ID} | User: ${d.USER_ID} | Address: ${d.ADDRESS}`;
          },
        },
      },
    },
  };

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
      {/* Biểu đồ cột */}
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ cột</h3>
        <Bar data={chartData} options={options} />
      </div>

      {/* Biểu đồ đường */}
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ đường</h3>
        <Line data={chartData} options={options} />
      </div>

      {/* Biểu đồ tròn */}
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ tròn</h3>
        <Pie data={pieData} options={options} />
      </div>

      {/* Thông tin tổng hợp */}
      <div
        style={{
          flex: "1 1 100%",
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
        }}
      >
        <p>
          <strong>Tổng năng lượng:</strong> {totalKwh} kWh
        </p>
        <p>
          <strong>Số thiết bị:</strong> {devices.length}
        </p>
      </div>
    </div>
  );
}

export default Dashboard;
