import React, { useState } from "react";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, devices, setDevices, addDevice }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: addresses[0],
    STATUS: "On",
    kWh: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleAdd = (e) => {
    e.preventDefault();
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");

    const newDevice = {
      ...form,
      USER_ID: user.USER_ID,
      CREATE_AT: new Date().toISOString().split("T")[0],
      kWh: Number(form.kWh) || 0,
    };

    addDevice(newDevice);
    setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On", kWh: 0 });
  };

  return (
    <div>
      {" "}
      <h2>Danh sách thiết bị</h2>
      <table
        border="1"
        cellPadding="5"
        style={{ marginBottom: "20px", width: "100%" }}
      >
        {" "}
        <thead>
          {" "}
          <tr>
            {" "}
            <th>Device Name</th> <th>Address</th> <th>Status</th> <th>kWh</th>{" "}
          </tr>{" "}
        </thead>{" "}
        <tbody>
          {devices.map((d) => (
            <tr key={d.DEVICE_ID}>
              {" "}
              <td>{d.DEVICE_NAME}</td> <td>{d.ADDRESS}</td> <td>{d.STATUS}</td>{" "}
              <td>{d.kWh}</td>{" "}
            </tr>
          ))}{" "}
        </tbody>{" "}
      </table>
      <h3>Thêm thiết bị mới</h3>
      <form
        onSubmit={handleAdd}
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "10px",
          maxWidth: "400px",
        }}
      >
        <input
          type="text"
          name="DEVICE_NAME"
          value={form.DEVICE_NAME}
          onChange={handleChange}
          placeholder="Tên thiết bị"
          required
        />
        <select name="ADDRESS" value={form.ADDRESS} onChange={handleChange}>
          {addresses.map((addr) => (
            <option key={addr} value={addr}>
              {addr}
            </option>
          ))}
        </select>
        <select name="STATUS" value={form.STATUS} onChange={handleChange}>
          <option value="On">On</option>
          <option value="Off">Off</option>
        </select>
        <input
          type="number"
          name="kWh"
          value={form.kWh}
          onChange={handleChange}
          placeholder="kWh"
        />
        <button type="submit">Thêm thiết bị</button>
      </form>
    </div>
  );
}

export default Device;
