import React from "react";

function DeviceCard({ device, onDelete, onEdit }) {
// Chọn màu theo status
const statusColor = device.status.toLowerCase() === "on" ? "#d4edda" : "#f8d7da";
const borderColor = device.status.toLowerCase() === "on" ? "#28a745" : "#dc3545";

return (
<div
style={{
padding: 15,
background: statusColor,
borderRadius: 12,
boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
border: `2px solid ${borderColor}`,
width: 220,
margin: 10,
position: "relative",
display: "flex",
flexDirection: "column",
justifyContent: "space-between",
}}
> <div>
<h4 style={{ marginBottom: 5 }}>{device.name}</h4>
<p style={{ margin: 2, fontSize: 14 }}><strong>Device ID:</strong> {device.device_id}</p>
<p style={{ margin: 2, fontSize: 14 }}><strong>User ID:</strong> {device.user_id}</p>
<p style={{ margin: 2, fontSize: 14 }}><strong>Điện:</strong> {device.kWh} kWh</p>
<p style={{ margin: 2, fontSize: 14 }}><strong>Thời gian:</strong> {device.time}</p>
<p style={{ margin: 2, fontSize: 14 }}><strong>Địa chỉ:</strong> {device.address}</p> </div>

```
  <div style={{ display: "flex", justifyContent: "space-between", marginTop: 10 }}>  
    <span style={{ fontWeight: "bold", color: borderColor }}>{device.status}</span>  

    <div>  
      <button onClick={onEdit} style={{ marginRight: 5, cursor: "pointer" }}>✏️</button>  
      <button onClick={onDelete} style={{ cursor: "pointer" }}>❌</button>  
    </div>  
  </div>  
</div>  


);
}

export default DeviceCard;
