import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import DeviceManager from "./components/DeviceManager";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [devices, setDevices] = useState([]);
  const [activeMenu, setActiveMenu] = useState("Dashboard");

  // Logout
  const handleLogout = () => {
    setUser(null);
    setDevices([]);
  };

  // Fetch devices khi user thay đổi
  useEffect(() => {
    const fetchDevices = async () => {
      if (!user?.USER_ID) return;
      try {
        const userId = Number(user.USER_ID); // đảm bảo là number
        const res = await fetch(
          `http://localhost:5000/api/devices/user/${userId}`
        );
        const data = await res.json();
        const colored = data.map((d, idx) => ({
          ...d,
          color:
            d.color || "#" + Math.floor(Math.random() * 16777215).toString(16),
          kWh: d.kWh || 0,
        }));
        setDevices(colored);
      } catch (err) {
        console.error("❌ Lỗi fetch devices:", err);
      }
    };
    fetchDevices();
  }, [user]);

  if (!user) return <Login onLogin={setUser} />;

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = (
        <Dashboard user={user} devices={devices} setDevices={setDevices} />
      );
      break;
    case "Device":
      content = <DeviceManager user={user} onDevicesUpdate={setDevices} />;
      break;
    case "Setting":
      content = <Setting />;
      break;
    default:
      content = (
        <Dashboard user={user} devices={devices} setDevices={setDevices} />
      );
  }

  return (
    <div className="app-container">
      {" "}
      <Sidebar
        activeMenu={activeMenu}
        setActiveMenu={setActiveMenu}
        user={user}
        onLogout={handleLogout}
      />{" "}
      <div className="main-content">
        {" "}
        <Header user={user} onLogout={handleLogout} />
        {content}{" "}
      </div>{" "}
    </div>
  );
}

export default App;
