// Logout
const handleLogout = () => {
setUser(null);
setActiveMenu("Dashboard");
};

// Nếu chưa login => hiển thị Login
if (!user) {
return <Login onLogin={setUser} />;
}

// Xác định nội dung hiển thị
let content;
switch (activeMenu) {
case "Dashboard":
content = <Dashboard devices={devices} />;
break;
case "Device":
content = <Device devices={devices} setDevices={setDevices} />;
break;
case "Setting":
content = <Setting />;
break;
default:
content = <Dashboard devices={devices} />;
}

return ( <div className="app-container">
{/* Sidebar cố định */} <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} /> <div className="main-content"> <Header user={user} onLogout={handleLogout} />
{content} </div> </div>
);
}

export default App;
