import React, { useState, useEffect } from "react";

function Device({ user, devices, setDevices }) {
  const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];
  const defaultForm = { DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" };
  const [form, setForm] = useState(defaultForm);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const randomColor = () =>
    "#" +
    Math.floor(Math.random() * 16777215)
      .toString(16)
      .padStart(6, "0");

  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      setLoading(true);
      setError(null);
      try {
        // ✅ Template literal đúng
        const res = await fetch(
          `http://localhost:5000/api/devices/with-power/${user.USER_ID}`
        );
        if (!res.ok) throw new Error(`Server responded ${res.status}`);
        const data = await res.json();
        const devicesWithColor = data.map((d) => ({
          ...d,
          color: randomColor(),
          kWh: d.kWh ?? 0,
        }));
        setDevices(devicesWithColor);
      } catch (err) {
        console.error("Lỗi fetch devices:", err);
        setError("Không thể tải danh sách thiết bị.");
      } finally {
        setLoading(false);
      }
    };

    fetchDevices();
  }, [user, setDevices]);

  const handleAdd = async () => {
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");
    try {
      const res = await fetch("http://localhost:5000/api/devices", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...form, USER_ID: user.USER_ID }),
      });
      if (!res.ok) throw new Error(`Server error: ${res.status}`);
      const newDevice = await res.json();
      newDevice.color = randomColor();
      newDevice.kWh = 0;
      setDevices([...devices, newDevice]);
      setForm(defaultForm);
    } catch (err) {
      console.error(err);
      alert("Lưu thiết bị thất bại!");
    }
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <h3>Thêm thiết bị</h3>
      <input
        type="text"
        placeholder="Tên thiết bị"
        value={form.DEVICE_NAME}
        onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
      />
      <select
        value={form.ADDRESS}
        onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
      >
        {addresses.map((a) => (
          <option key={a} value={a}>
            {a}
          </option>
        ))}
      </select>
      <select
        value={form.STATUS}
        onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
      >
        <option>On</option>
        <option>Off</option>
      </select>
      <button onClick={handleAdd}>Lưu thiết bị</button>

      {loading && <p>Đang tải danh sách thiết bị...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          marginTop: "20px",
          gap: "16px",
        }}
      >
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "16px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Trạng thái: {d.STATUS === "On" ? "Bật" : "Tắt"}</p>
            <p>Điện năng (kWh): {d.kWh}</p>
            <p>
              Thời gian:{" "}
              {d.CREATE_AT
                ? new Date(d.CREATE_AT).toLocaleString("vi-VN")
                : "-"}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
