import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, onDevicesUpdate }) {
const [devices, setDevices] = useState([]);
const [form, setForm] = useState({
DEVICE_NAME: "",
kWh: "",
ADDRESS: addresses[0],
STATUS: "On",
CREATE_AT: "",
});

// Load devices từ server
const fetchDevices = async () => {
try {
const res = await axios.get("[http://localhost:5000/api/devices](http://localhost:5000/api/devices)");
const devicesWithColor = res.data.map((d) => ({
...d,
color: "#" + Math.floor(Math.random() * 16777215).toString(16),
}));
setDevices(devicesWithColor);
if (onDevicesUpdate) onDevicesUpdate(devicesWithColor);
} catch (err) {
console.error("Error fetching devices:", err);
}
};

useEffect(() => {
fetchDevices();
}, []);

// Thêm device mới
const handleAdd = async () => {
if (!form.DEVICE_NAME || !form.kWh || !form.CREATE_AT)
return alert("Nhập đủ Tên thiết bị, kWh và Ngày");

try {
  const res = await axios.post("http://localhost:5000/api/devices", {
    DEVICE_NAME: form.DEVICE_NAME,
    ADDRESS: form.ADDRESS,
    STATUS: form.STATUS,
    USER_ID: user.USER_ID,
    kWh: Number(form.kWh),
    CREATE_AT: form.CREATE_AT,
  });

  const newDevice = {
    ...res.data,
    color: "#" + Math.floor(Math.random() * 16777215).toString(16),
  };

  const updatedDevices = [...devices, newDevice];
  setDevices(updatedDevices);
  if (onDevicesUpdate) onDevicesUpdate(updatedDevices);

  setForm({
    DEVICE_NAME: "",
    kWh: "",
    ADDRESS: addresses[0],
    STATUS: "On",
    CREATE_AT: "",
  });
} catch (err) {
  console.error("Error saving device:", err);
  alert("Lưu thiết bị vào SQL thất bại!");
}


};

return (
<div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
{/* Form thêm thiết bị */}
<div
style={{
padding: "16px",
border: "1px solid #ccc",
borderRadius: "8px",
maxWidth: "400px",
}}
> <h4>Thêm thiết bị mới</h4>
<input
type="text"
placeholder="Tên thiết bị"
value={form.DEVICE_NAME}
onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
style={{ width: "100%", marginBottom: "8px" }}
/>
<input
type="number"
placeholder="kWh"
value={form.kWh}
onChange={(e) => setForm({ ...form, kWh: e.target.value })}
style={{ width: "100%", marginBottom: "8px" }}
/>
<input
type="text"
placeholder="Ngày (YYYY-MM-DD)"
value={form.CREATE_AT}
onChange={(e) => setForm({ ...form, CREATE_AT: e.target.value })}
style={{ width: "100%", marginBottom: "8px" }}
/>
<select
value={form.ADDRESS}
onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
style={{ width: "100%", marginBottom: "8px" }}
>
{addresses.map((a) => ( <option key={a} value={a}>
{a} </option>
))} </select>
<select
value={form.STATUS}
onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
style={{ width: "100%", marginBottom: "8px" }}
> <option>On</option> <option>Off</option> </select>
<button onClick={handleAdd} style={{ width: "100%" }}>
Lưu vào SQL & Thêm thiết bị </button> </div>


  {/* Device cards */}
  <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
    {devices.map((d) => (
      <div
        key={d.DEVICE_ID}
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          borderLeft: `4px solid ${d.color || "#000"}`,
          minWidth: "200px",
        }}
      >
        <h4>{d.DEVICE_NAME}</h4>
        <p>kWh: {d.kWh}</p>
        <p>Địa chỉ: {d.ADDRESS}</p>
        <p>Status: {d.STATUS}</p>
        <p>Ngày: {d.CREATE_AT}</p>
      </div>
    ))}
  </div>
</div>


);
}

export default Device;
