function DeviceCard({ device, onDelete, onEdit }) {
  return (
    <div className="device-card" style={{ position: "relative" }}>
      <h3>{device.name}</h3>
      <p>⚡ {device.kWh} kWh</p>
      <p>⏱ {device.time}</p>

      <button className="edit-btn" onClick={() => onEdit(device)}>
        ✏️
      </button>

      <button className="delete-btn" onClick={onDelete}>
        ❌
      </button>

      <style>
        {`
          .edit-btn, .delete-btn {
            position: absolute;
            top: 10px;
            opacity: 0;
            transition: 0.2s;
            background: transparent;
            border: none;
            cursor: pointer;
            font-size: 18px;
          }
          .edit-btn { right: 40px; }
          .delete-btn { right: 10px; }
          .device-card:hover .edit-btn,
          .device-card:hover .delete-btn {
            opacity: 1;
          }
        `}
      </style>
    </div>
  );
}
