/* App.js */
import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null); // null nghĩa là chưa login
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  // Lấy dữ liệu devices từ server (SQL Server qua API)
  useEffect(() => {
    const fetchDevices = async () => {
      try {
        const res = await fetch(
          "[http://localhost:5000/api/devices](http://localhost:5000/api/devices)"
        );
        const data = await res.json();
        // Gán màu ngẫu nhiên nếu chưa có
        const colored = data.map((d) => ({
          ...d,
          color:
            d.color || "#" + Math.floor(Math.random() * 16777215).toString(16),
        }));
        setDevices(colored);
      } catch (err) {
        console.error("Error fetching devices:", err);
      }
    };

    ```
fetchDevices();
const interval = setInterval(fetchDevices, 5000); // cập nhật 5s/lần
return () => clearInterval(interval);
```;
  }, []);

  // Logout
  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
  };

  // Nếu chưa login => hiển thị Login
  if (!user) {
    return <Login onLogin={setUser} />;
  }

  // Xác định nội dung hiển thị
  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = <Device devices={devices} setDevices={setDevices} />;
      break;
    case "Setting":
      content = <Setting />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  return (
    <div className="app-container">
      {/* Sidebar cố định */}{" "}
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />{" "}
      <div className="main-content">
        {" "}
        <Header user={user} onLogout={handleLogout} />
        {content}{" "}
      </div>{" "}
    </div>
  );
}

export default App;
