/* Device.js */
import React, { useState } from "react";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ devices, setDevices }) {
const [form, setForm] = useState({
name: "",
kWh: "",
time: "",
address: addresses[0],
status: "On",
date: new Date().toISOString().split("T")[0],
});

const randomColor = () => {
const letters = "0123456789ABCDEF";
let color = "#";
for (let i = 0; i < 6; i++) {
color += letters[Math.floor(Math.random() * 16)];
}
return color;
};

const handleAdd = () => {
if (!form.name || !form.kWh) return;
setDevices([
...devices,
{
...form,
id: devices.length + 1,
device_id: `D${String(devices.length + 1).padStart(3, "0")}`,
user_id: `U${String(devices.length + 1).padStart(3, "0")}`,
color: randomColor(),
},
]);
setForm({
name: "",
kWh: "",
time: "",
address: addresses[0],
status: "On",
date: new Date().toISOString().split("T")[0],
});
};

return ( <div className="device-grid"> <div className="device-card"> <h4>Thêm thiết bị mới</h4>
<input
type="text"
placeholder="Tên thiết bị"
value={form.name}
onChange={(e) => setForm({ ...form, name: e.target.value })}
/>
<input
type="number"
placeholder="kWh"
value={form.kWh}
onChange={(e) => setForm({ ...form, kWh: e.target.value })}
/>
<input
type="text"
placeholder="Thời gian"
value={form.time}
onChange={(e) => setForm({ ...form, time: e.target.value })}
/>
<select
value={form.address}
onChange={(e) => setForm({ ...form, address: e.target.value })}
>
{addresses.map((a) => ( <option key={a} value={a}>
{a} </option>
))} </select>
<select
value={form.status}
onChange={(e) => setForm({ ...form, status: e.target.value })}
> <option>On</option> <option>Off</option> </select> <button className="add-device-btn" onClick={handleAdd}>
Thêm thiết bị </button> </div>

