// Device.js
import React from "react";

function Device({ user, devices }) {
  if (!devices || devices.length === 0) {
    return <p>Không có thiết bị nào cho user {user.USER_NAME}</p>;
  }

  return (
    <div className="device-list">
      <h2>Danh sách thiết bị của {user.USER_NAME}</h2>
      <table
        border="1"
        cellPadding="8"
        style={{ borderCollapse: "collapse", width: "100%" }}
      >
        <thead>
          <tr>
            <th>ID</th>
            <th>Tên thiết bị</th>
            <th>Trạng thái</th>
            <th>Công suất (W)</th>
            <th>Điện áp (V)</th>
            <th>Dòng điện (A)</th>
          </tr>
        </thead>
        <tbody>
          {devices.map((d) => (
            <tr key={d.DEVICE_ID}>
              <td>{d.DEVICE_ID}</td>
              <td>{d.DEVICE_NAME}</td>
              <td>{d.STATUS}</td>
              <td>{d.POWER ?? "-"}</td>
              <td>{d.VOLTAGE ?? "-"}</td>
              <td>{d.AMPERAGE ?? "-"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Device;
