import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";
import Login from "./components/Login";
import axios from "axios";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  // Load devices từ server
  const fetchDevices = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/devices");
      const devicesWithColor = res.data.map(d => ({
        ...d,
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setDevices(devicesWithColor);
    } catch (err) {
      console.error("Lỗi fetch devices:", err);
    }
  };

  // Khi login xong hoặc App load
  useEffect(() => {
    if (user) fetchDevices();
  }, [user]);

  // Logout
  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
    setDevices([]);
  };

  // Nếu chưa login → hiện Login
  if (!user) {
    return <Login onLogin={setUser} />;
  }

  // Render nội dung theo menu
  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = <Device user={user} devices={devices} setDevices={setDevices} />;
      break;
    case "Setting":
      content = <Setting user={user} />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  return (
    <div className="app-container" style={{ display: "flex", height: "100vh" }}>
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
      <div className="main-content" style={{ flex: 1, padding: "16px", overflowY: "auto" }}>
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
