import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device() {
const [devices, setDevices] = useState([]);
const [form, setForm] = useState({
DEVICE_NAME: "",
kWh: "",
time: "",
ADDRESS: addresses[0],
STATUS: "On",
});

// Fetch dữ liệu từ SQL Server qua API Node.js
useEffect(() => {
axios.get("[http://localhost:5000/api/devices](http://localhost:5000/api/devices)")
.then((res) => setDevices(res.data))
.catch((err) => console.error("Error loading devices:", err));
}, []);

// Tạo màu ngẫu nhiên cho card
const randomColor = () => {
const letters = "0123456789ABCDEF";
let color = "#";
for (let i = 0; i < 6; i++) {
color += letters[Math.floor(Math.random() * 16)];
}
return color;
};

// Thêm thiết bị mới vào state local
const handleAdd = () => {
if (!form.DEVICE_NAME || !form.kWh) return;
setDevices([
...devices,
{
...form,
DEVICE_ID: devices.length + 1,
USER_ID: 300000, // Mặc định hoặc lấy từ login
color: randomColor(),
},
]);
setForm({
DEVICE_NAME: "",
kWh: "",
time: "",
ADDRESS: addresses[0],
STATUS: "On",
});
};

return (
<div className="device-grid" style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
{/* Form thêm thiết bị */}
<div className="device-card" style={{ padding: "16px", border: "1px solid #ccc", borderRadius: "8px" }}> <h4>Thêm thiết bị mới</h4>
<input
type="text"
placeholder="Tên thiết bị"
value={form.DEVICE_NAME}
onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
/>
<input
type="number"
placeholder="kWh"
value={form.kWh}
onChange={(e) => setForm({ ...form, kWh: e.target.value })}
/>
<input
type="text"
placeholder="Thời gian"
value={form.time}
onChange={(e) => setForm({ ...form, time: e.target.value })}
/>
<select
value={form.ADDRESS}
onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
>
{addresses.map((a) => (<option key={a} value={a}>{a}</option>))} </select>
<select
value={form.STATUS}
onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
> <option>On</option> <option>Off</option> </select>
<button onClick={handleAdd} style={{ marginTop: "8px" }}>Thêm thiết bị</button> </div>

