import React, { useState, useEffect } from "react";
import axios from "axios";

function Device({ user, devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: "Phòng khách",
    STATUS: "On",
  });

  const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

  // Lấy danh sách device từ backend khi user thay đổi
  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/devices-with-power/${user.USER_ID}`
        );

        const devicesWithColor = res.data.map((d) => ({
          ...d,
          color: "#" + Math.floor(Math.random() * 16777215).toString(16),
        }));

        setDevices(devicesWithColor);
      } catch (err) {
        console.error("Error fetching devices:", err);
        setDevices([]);
      }
    };

    fetchDevices();
  }, [user, setDevices]);

  // Thêm device mới (chỉ trên frontend, có thể mở rộng POST lên backend)
  const handleAdd = () => {
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");

    const newDevice = {
      DEVICE_ID: devices.length + 1,
      ...form,
      kWh: 0, // Mới thêm chưa có log
      CREATE_AT: new Date(),
      color: "#" + Math.floor(Math.random() * 16777215).toString(16),
    };

    setDevices([...devices, newDevice]);
    setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <h3>Thêm Device</h3>
      <input
        type="text"
        placeholder="Tên thiết bị"
        value={form.DEVICE_NAME}
        onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
      />
      <select
        value={form.ADDRESS}
        onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
      >
        {addresses.map((a) => (
          <option key={a} value={a}>
            {a}
          </option>
        ))}
      </select>
      <select
        value={form.STATUS}
        onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
      >
        <option>On</option>
        <option>Off</option>
      </select>
      <button onClick={handleAdd}>Lưu thiết bị</button>

      <div
        style={{
          display: "flex",
          flexWrap: "wrap",
          marginTop: "20px",
          gap: "16px",
        }}
      >
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "16px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>kWh: {d.kWh != null ? d.kWh : 0}</p>
            <p>
              Time: {d.CREATE_AT ? new Date(d.CREATE_AT).toLocaleString() : "-"}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
