import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import DeviceManager from "./components/DeviceManager";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
const [user, setUser] = useState(null); // null = chưa login
const [activeMenu, setActiveMenu] = useState("Dashboard");
const [devices, setDevices] = useState([]);

// Logout function
const handleLogout = () => {
setUser(null);
setDevices([]);
setActiveMenu("Dashboard");
};

// Nếu chưa login → show login
if (!user) return <Login onLogin={setUser} />;

// Lấy devices khi login hoặc user thay đổi
useEffect(() => {
async function fetchDevices() {
if (!user?.USER_ID) return;
try {
const res = await fetch(
`http://localhost:5000/api/devices/user/${user.USER_ID}`
);
const data = await res.json();
setDevices(data);
} catch (err) {
console.error("❌ Lỗi khi fetch devices:", err);
}
}
fetchDevices();
}, [user]);

// Xác định content theo menu
const renderContent = () => {
switch (activeMenu) {
case "Dashboard":
return <Dashboard devices={devices} />;
case "Device":
return <DeviceManager user={user} onDevicesUpdate={setDevices} />;
case "Setting":
return <Setting />;
default:
return <Dashboard devices={devices} />;
}
};

return ( <div className="app-container">
<Sidebar
activeMenu={activeMenu}
setActiveMenu={setActiveMenu}
user={user}
onLogout={handleLogout} // thêm logout vào Sidebar
/> <div className="main-content"> <Header user={user} onLogout={handleLogout} />
{renderContent()} </div> </div>
);
}

export default App;
