import React, { useState, useEffect, useCallback } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Bar, Line, Pie } from "react-chartjs-2";
import DeviceCard from "./DeviceCard";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

const REFRESH_OPTIONS = [
  { label: "5 giây", value: 5000 },
  { label: "10 giây", value: 10000 },
  { label: "30 giây", value: 30000 },
  { label: "1 phút", value: 60000 },
  { label: "5 phút", value: 300000 },
  { label: "10 phút", value: 600000 },
  { label: "30 phút", value: 1800000 },
  { label: "1 giờ", value: 3600000 },
  { label: "3 giờ", value: 10800000 },
  { label: "5 giờ", value: 18000000 },
  { label: "10 giờ", value: 36000000 },
  { label: "24 giờ", value: 86400000 },
];

const DEFAULT_COLORS = ["#4caf50", "#2196f3", "#ff9800", "#9c27b0", "#f44336"];

function Dashboard() {
  const [allDevices, setAllDevices] = useState([]);
  const [visibleDevices, setVisibleDevices] = useState({});
  const [timeRange, setTimeRange] = useState("day");
  const [customRange, setCustomRange] = useState([null, null]);
  const [refreshInterval, setRefreshInterval] = useState(10000);

  const [startDate, endDate] = customRange;

  // fetch dữ liệu từ server
  const fetchData = useCallback(async () => {
    try {
      const res = await fetch(
        "[http://localhost:5000/api/devices](http://localhost:5000/api/devices)"
      );
      const data = await res.json();

      // map màu và timestamp
      const devicesWithExtras = data.map((d, idx) => ({
        ...d,
        color: DEFAULT_COLORS[idx % DEFAULT_COLORS.length],
        timestamp: d.CREATE_AT || new Date().toISOString(),
      }));

      setAllDevices(devicesWithExtras);
      // mặc định visible = true cho tất cả
      const visibleMap = {};
      devicesWithExtras.forEach((d) => (visibleMap[d.DEVICE_ID] = true));
      setVisibleDevices(visibleMap);
    } catch (err) {
      console.error(err);
    }
  }, []);

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, refreshInterval);
    return () => clearInterval(interval);
  }, [fetchData, refreshInterval]);

  const handleToggleDevice = (id) =>
    setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));

  const filterDevices = useCallback(() => {
    const now = new Date();
    return allDevices.filter((d) => {
      if (!visibleDevices[d.DEVICE_ID]) return false;
      const deviceDate = new Date(d.timestamp);
      if (timeRange === "day")
        return deviceDate.toDateString() === now.toDateString();
      if (timeRange === "week") {
        const weekStart = new Date(now);
        weekStart.setDate(now.getDate() - now.getDay());
        return deviceDate >= weekStart && deviceDate <= now;
      }
      if (timeRange === "month") {
        return (
          deviceDate.getMonth() === now.getMonth() &&
          deviceDate.getFullYear() === now.getFullYear()
        );
      }
      if (timeRange === "custom" && startDate && endDate) {
        const start = new Date(startDate);
        const end = new Date(endDate);
        end.setHours(23, 59, 59, 999);
        return deviceDate >= start && deviceDate <= end;
      }
      return true;
    });
  }, [allDevices, visibleDevices, timeRange, startDate, endDate]);

  const filteredDevices = filterDevices();

  const totalKwh = filteredDevices.reduce(
    (sum, d) => sum + Number(d.kWh || 0),
    0
  );

  const chartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color),
        borderColor: filteredDevices.map((d) => d.color),
        borderWidth: 2,
      },
    ],
  };

  const lineChartData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        label: "kWh",
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        borderColor: filteredDevices.map((d) => d.color),
        borderWidth: 3,
        fill: false,
        tension: 0.4,
      },
    ],
  };

  const pieData = {
    labels: filteredDevices.map((d) => d.DEVICE_NAME),
    datasets: [
      {
        data: filteredDevices.map((d) => Number(d.kWh || 0)),
        backgroundColor: filteredDevices.map((d) => d.color),
        borderColor: "#fff",
        borderWidth: 4,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const d = filteredDevices[context.dataIndex];
            if (!d) return "";
            return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS || ""} | ${
              d.STATUS || ""
            }`;
          },
        },
      },
      legend: { display: false },
    },
    scales: { y: { beginAtZero: true } },
  };

  return (
    <div
      style={{ padding: 16, display: "flex", flexDirection: "column", gap: 24 }}
    >
      {/* Filters */}
      <div
        style={{ display: "flex", gap: 16, flexWrap: "wrap", marginBottom: 12 }}
      >
        {" "}
        <div>
          {" "}
          <label>Time Range:</label>
          <select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
            style={{ marginLeft: 8, padding: "4px 8px" }}
          >
            {" "}
            <option value="day">Hôm nay</option>{" "}
            <option value="week">Tuần này</option>{" "}
            <option value="month">Tháng này</option>{" "}
            <option value="custom">Tùy chỉnh</option>{" "}
          </select>{" "}
        </div>
        {timeRange === "custom" && (
          <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
            <DatePicker
              selectsRange
              startDate={startDate}
              endDate={endDate}
              onChange={(update) => setCustomRange(update)}
              isClearable
              placeholderText="Chọn khoảng ngày"
            />{" "}
          </div>
        )}{" "}
        <div>
          {" "}
          <label>Refresh:</label>
          <select
            value={refreshInterval}
            onChange={(e) => setRefreshInterval(Number(e.target.value))}
            style={{ marginLeft: 8, padding: "4px 8px" }}
          >
            {REFRESH_OPTIONS.map((o) => (
              <option key={o.value} value={o.value}>
                {o.label}
              </option>
            ))}{" "}
          </select>{" "}
        </div>{" "}
      </div>

      {/* Device cards */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 16 }}>
        {filteredDevices.map((d) => (
          <DeviceCard key={d.DEVICE_ID} device={d} />
        ))}
      </div>

      {/* Toggle buttons */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 12 }}>
        {allDevices.map((d) => (
          <button
            key={d.DEVICE_ID}
            onClick={() => handleToggleDevice(d.DEVICE_ID)}
            style={{
              padding: "6px 12px",
              borderRadius: 8,
              border: visibleDevices[d.DEVICE_ID]
                ? `2px solid ${d.color}`
                : "1px solid #ccc",
              backgroundColor: visibleDevices[d.DEVICE_ID] ? d.color : "#fff",
              color: visibleDevices[d.DEVICE_ID] ? "#fff" : "#000",
              cursor: "pointer",
              transition: "0.2s",
            }}
          >
            {d.DEVICE_NAME}
          </button>
        ))}
      </div>

      {/* Total energy */}
      <div
        style={{
          backgroundColor: "#fff",
          padding: 16,
          borderRadius: 12,
          boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          minWidth: 200,
        }}
      >
        <h4>Tổng năng lượng</h4>
        <p style={{ fontSize: 24, fontWeight: "bold", margin: 0 }}>
          {totalKwh} kWh
        </p>
      </div>

      {/* Charts */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: 24 }}>
        <div
          style={{
            flex: "1 1 400px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ cột</h4>
          <Bar data={chartData} options={chartOptions} />
        </div>
        <div
          style={{
            flex: "1 1 400px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ đường</h4>
          <Line data={lineChartData} options={chartOptions} />
        </div>
        <div
          style={{
            flex: "1 1 300px",
            height: 300,
            backgroundColor: "#fff",
            borderRadius: 12,
            padding: 16,
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
          }}
        >
          <h4>Biểu đồ tròn</h4>
          <Pie
            data={pieData}
            options={{ ...chartOptions, maintainAspectRatio: true }}
          />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
