import React, { useState } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
Chart as ChartJS,
CategoryScale,
LinearScale,
BarElement,
LineElement,
PointElement,
ArcElement,
Tooltip,
Legend,
} from "chart.js";

ChartJS.register(
CategoryScale,
LinearScale,
BarElement,
LineElement,
PointElement,
ArcElement,
Tooltip,
Legend
);

function Dashboard({ devices = [] }) {
const [visibleDevices, setVisibleDevices] = useState(
devices.reduce((acc, d) => ({ ...acc, [d.DEVICE_ID]: true }), {})
);

const handleToggleDevice = (id) => {
setVisibleDevices((prev) => ({ ...prev, [id]: !prev[id] }));
};

const filteredDevices = devices.filter((d) => visibleDevices[d.DEVICE_ID]);

const chartLabels = filteredDevices.map((d) => d.DEVICE_NAME);

const chartData = {
labels: chartLabels,
datasets: [
{
label: "kWh",
data: filteredDevices.map((d) => Number(d.kWh || 0)),
backgroundColor: filteredDevices.map((d) => d.color || "#af4c8bff"),
borderColor: "#333",
borderWidth: 2,
tension: 0.4,
},
],
};

const pieData = {
labels: chartLabels,
datasets: [
{
data: filteredDevices.map((d) => Number(d.kWh || 0)),
backgroundColor: filteredDevices.map((d) => d.color || "#4caf50"),
borderColor: "#fff",
borderWidth: 4,
},
],
};

const options = {
responsive: true,
maintainAspectRatio: false,
plugins: {
tooltip: {
callbacks: {
label: function (context) {
const d = filteredDevices[context.dataIndex];
return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.ADDRESS} | ${d.STATUS}`;
},
},
},
legend: { display: false },
},
};

return (
<div style={{ display: "flex", flexDirection: "column", gap: "24px", padding: "16px" }}>
{/* Legend toggle giống Pie chart */}
<div style={{ display: "flex", gap: "12px", flexWrap: "wrap", marginBottom: "12px" }}>
{devices.map((d) => (
<div
key={d.DEVICE_ID}
onClick={() => handleToggleDevice(d.DEVICE_ID)}
style={{
display: "flex",
alignItems: "center",
gap: "4px",
cursor: "pointer",
opacity: visibleDevices[d.DEVICE_ID] ? 1 : 0.4,
}}
>
<span
style={{
width: "16px",
height: "16px",
backgroundColor: d.color || "#4caf50",
borderRadius: "2px",
}}
/> <span>{d.DEVICE_NAME}</span> </div>
))} </div>
``
  {/* Bar Chart */}
  <div
    style={{
      flex: "1 1 400px",
      height: "300px",
      backgroundColor: "#fff",
      borderRadius: "12px",
      padding: "16px",
      boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    }}
  >
    <h4>Biểu đồ cột</h4>
    <Bar data={chartData} options={options} />
  </div>

  {/* Line Chart */}
  <div
    style={{
      flex: "1 1 400px",
      height: "300px",
      backgroundColor: "#fff",
      borderRadius: "12px",
      padding: "16px",
      boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    }}
  >
    <h4>Biểu đồ đường</h4>
    <Line data={chartData} options={options} />
  </div>

  {/* Pie Chart */}
  <div
    style={{
      flex: "1 1 300px",
      height: "300px",
      backgroundColor: "#fff",
      borderRadius: "12px",
      padding: "16px",
      boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
    }}
  >
    <h4>Biểu đồ tròn</h4>
    <div style={{ width: "80%", height: "80%" }}>
      <Pie data={pieData} options={{ ...options, maintainAspectRatio: true }} />
    </div>
  </div>
</div>


);
}

export default Dashboard;
