import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, setDevices }) {
  const [devices, setLocalDevices] = useState([]);
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  // Load devices
  const fetchDevices = async () => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/devices/user/${user.USER_ID}`
      );
      setLocalDevices(res.data);
      setDevices(res.data);
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

  useEffect(() => {
    if (user) fetchDevices();
  }, [user]);

  // Thêm device mới
  const handleAdd = async () => {
    if (!form.DEVICE_NAME || !form.ADDRESS || !form.STATUS)
      return alert("Nhập đủ thông tin");

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        USER_ID: user.USER_ID,
        DEVICE_NAME: form.DEVICE_NAME,
        ADDRESS: form.ADDRESS,
        STATUS: form.STATUS,
      });

      const updatedDevices = [...devices, res.data];
      setLocalDevices(updatedDevices);
      setDevices(updatedDevices);

      setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
    } catch (err) {
      console.error("Error adding device:", err);
      alert("Thêm thiết bị thất bại!");
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      {/* Form thêm thiết bị */}
      <div style={{ padding: "16px", border: "1px solid #ccc", borderRadius: "8px", maxWidth: "400px" }}>
        <h4>Thêm thiết bị mới</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          {addresses.map((a) => <option key={a} value={a}>{a}</option>)}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd} style={{ width: "100%" }}>
          Thêm thiết bị
        </button>
      </div>

      {/* Device cards */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div key={d.DEVICE_ID} style={{ padding: "16px", border: "1px solid #ccc", borderRadius: "8px", minWidth: "200px" }}>
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
