import React from "react";
import { Bar, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function Charts({ data }) {
  const labels = data.map((d) => d.date);
  const chartData = {
    labels,
    datasets: [
      {
        type: "bar",
        label: "Consumption (kWh)",
        data: data.map((d) => d.kWh),
        backgroundColor: "rgba(75,192,192,0.6)",
      },
      {
        type: "line",
        label: "Trend",
        data: data.map((d) => d.kWh),
        borderColor: "rgba(255,99,132,1)",
        borderWidth: 2,
        fill: false,
      },
    ],
  };

  return (
    <div style={{ display: "flex", gap: 20 }}>
      <div style={{ flex: 1 }}>
        <Bar data={chartData} />
      </div>
      <div style={{ flex: 1 }}>
        <Line data={chartData} />
      </div>
    </div>
  );
}

export default Charts;
