// App.js
import React, { useState } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([
    { DEVICE_ID: 1, USER_ID: 101, DEVICE_NAME: "Máy lạnh", ADDRESS: "Phòng khách", STATUS: "On", kWh: 12.5, CREATE_AT: "2025-11-25", color: "#4caf50" },
    { DEVICE_ID: 2, USER_ID: 101, DEVICE_NAME: "Tủ lạnh", ADDRESS: "Nhà bếp", STATUS: "On", kWh: 8.2, CREATE_AT: "2025-11-24", color: "#2196f3" },
    { DEVICE_ID: 3, USER_ID: 102, DEVICE_NAME: "Quạt", ADDRESS: "Phòng ngủ", STATUS: "Off", kWh: 3.7, CREATE_AT: "2025-11-23", color: "#ff9800" },
  ]);

  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
  };

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = <Device user={user} onDevicesUpdate={setDevices} />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="app-container">
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
