import React, { useState, useEffect } from "react";
import axios from "axios";
import Device from "./Device";
import Dashboard from "./cDashboard";

function App() {
  const [devices, setDevices] = useState([]);
  const [user, setUser] = useState({ USER_ID: 1, NAME: "Admin" }); // giả lập user

  // Load devices từ server
  const fetchDevices = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/devices");
      // thêm màu cho biểu đồ và device card
      const devicesWithColor = res.data.map((d) => ({
        ...d,
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      }));
      setDevices(devicesWithColor);
    } catch (err) {
      console.error("Lỗi fetch devices:", err);
    }
  };

  useEffect(() => {
    fetchDevices();
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Dashboard Năng Lượng & Quản Lý Thiết Bị</h1>
      <Dashboard devices={devices} />
      <hr style={{ margin: "20px 0" }} />
      <Device user={user} devices={devices} setDevices={setDevices} />
    </div>
  );
}

export default App;
