import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Login from "./components/Login";
import Setting from "./components/Setting";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [activeMenu, setActiveMenu] = useState("Dashboard");
  const [devices, setDevices] = useState([]);

  // Lấy devices + kWh khi user thay đổi
  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/devices/with-power/${user.USER_ID}`
        );

        if (!res.ok) {
          console.warn(`Server responded with status: ${res.status}`);
          setDevices([]);
          return;
        }

        const data = await res.json();

        // Tính kWh từ POWER_LOG (giả sử dữ liệu join trả về nhiều log)
        const devicesMap = {};
        data.forEach((row) => {
          const id = row.DEVICE_ID;
          if (!devicesMap[id]) {
            devicesMap[id] = { ...row, kWh: 0, color: "#" + Math.floor(Math.random() * 16777215).toString(16) };
          }
          if (row.POWER && row.CREATE_AT) {
            // Giả lập Δt = 1h nếu không có log trước (có thể tinh chỉnh sau)
            devicesMap[id].kWh += row.POWER / 1000;
          }
        });

        setDevices(Object.values(devicesMap));
      } catch (err) {
        console.error("Error fetching devices:", err);
        setDevices([]);
      }
    };

    fetchDevices();
  }, [user]);

  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
    setDevices([]);
  };

  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = <Device user={user} devices={devices} setDevices={setDevices} />;
      break;
    case "Setting":
      content = <Setting user={user} />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  if (!user) return <Login onLogin={setUser} />;

  return (
    <div className="app-container">
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
