// Device.js
import React from "react";

function Device({ user, devices, setDevices }) {
  if (!devices || devices.length === 0) {
    return <p>Không có thiết bị nào cho user {user.USER_NAME}</p>;
  }

  return (
    <div className="device-list">
      <h2>Danh sách thiết bị của {user.USER_NAME}</h2>
      <ul>
        {devices.map((d) => (
          <li key={d.DEVICE_ID} style={{ color: d.color }}>
            <strong>{d.DEVICE_NAME}</strong>
            <span> | Trạng thái: {d.STATUS}</span>
            {d.POWER && <span> | Công suất: {d.POWER} W</span>}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Device;
