import React from "react";

function Sidebar({ activeMenu, setActiveMenu }) {
  const menus = ["Dashboard", "Device", "Setting"];

  return (
    <div
      style={{
        width: 200,
        background: "#1f2937",
        color: "#fff",
        minHeight: "100vh",
        padding: 20,
      }}
    >
      <h2>Energy App</h2>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {menus.map((menu) => (
          <li
            key={menu}
            onClick={() => setActiveMenu(menu)}
            style={{
              padding: 10,
              margin: "10px 0",
              cursor: "pointer",
              backgroundColor: activeMenu === menu ? "#374151" : "transparent",
            }}
          >
            {menu}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sidebar;
