import React, { useState } from "react";
import DeviceCard from "./DeviceCard";

function Device() {
  const [devices, setDevices] = useState([
    { id: 1, name: "Máy lạnh", kWh: 12, time: "10:00 - 12:00" },
    { id: 2, name: "Tivi", kWh: 5, time: "18:00 - 20:00" },
  ]);

  // State thêm
  const [newName, setNewName] = useState("");
  const [newKwh, setNewKwh] = useState("");
  const [newTime, setNewTime] = useState("");

  // State edit
  const [editing, setEditing] = useState(null);
  const [editName, setEditName] = useState("");
  const [editKwh, setEditKwh] = useState("");
  const [editTime, setEditTime] = useState("");

  // Thêm thiết bị
  const addDevice = () => {
    if (!newName || !newKwh || !newTime) {
      alert("Nhập đầy đủ thông tin!");
      return;
    }

    const newDevice = {
      id: Date.now(),
      name: newName,
      kWh: Number(newKwh),
      time: newTime,
    };

    setDevices([...devices, newDevice]);

    // Reset input
    setNewName("");
    setNewKwh("");
    setNewTime("");
  };

  // Mở modal sửa thiết bị
  const openEdit = (device) => {
    setEditing(device.id);
    setEditName(device.name);
    setEditKwh(device.kWh);
    setEditTime(device.time);
  };

  // Lưu sửa
  const saveEdit = () => {
    const updated = devices.map((d) =>
      d.id === editing
        ? { ...d, name: editName, kWh: Number(editKwh), time: editTime }
        : d
    );

    setDevices(updated);
    setEditing(null);
  };

  // Xóa thiết bị
  const deleteDevice = (id) => {
    setDevices(devices.filter((d) => d.id !== id));
  };

  return (
    <div>
      <h2 style={{ marginLeft: 20 }}>Device</h2>

      {/* Form thêm thiết bị */}
      <div style={{ margin: 20 }}>
        <input
          type="text"
          placeholder="Tên thiết bị..."
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          style={{ padding: 8, marginRight: 10 }}
        />

        <input
          type="number"
          placeholder="kWh"
          value={newKwh}
          onChange={(e) => setNewKwh(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: 80 }}
        />

        <input
          type="text"
          placeholder="Thời gian (VD: 10:00 - 12:00)"
          value={newTime}
          onChange={(e) => setNewTime(e.target.value)}
          style={{ padding: 8, marginRight: 10, width: 180 }}
        />

        <button className="add-device-btn" onClick={addDevice}>
          + Add Device
        </button>
      </div>

      {/* Danh sách thiết bị */}
      <div className="device-grid">
        {devices.map((d) => (
          <DeviceCard
            key={d.id}
            device={d}
            onDelete={() => deleteDevice(d.id)}
            onEdit={() => openEdit(d)}
          />
        ))}
      </div>

      {/* MODAL EDIT */}
      {editing && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: 20,
              width: 350,
              borderRadius: 10,
            }}
          >
            <h3>Sửa thiết bị</h3>

            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
            />

            <input
              type="number"
              value={editKwh}
              onChange={(e) => setEditKwh(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
            />

            <input
              type="text"
              value={editTime}
              onChange={(e) => setEditTime(e.target.value)}
              style={{ padding: 8, width: "100%", marginBottom: 10 }}
            />

            <button onClick={saveEdit} style={{ marginRight: 10 }}>
              Lưu
            </button>
            <button onClick={() => setEditing(null)}>Hủy</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Device;
