import React, { useState } from "react";

function Device({ devices, setDevices }) {
  const [newDevice, setNewDevice] = useState({
    device_id: "",
    user_id: "",
    name: "",
    kWh: 0,
    time: "",
    address: "",
    status: "Off",
    date: "",
  });

  const handleAdd = () => {
    setDevices([...devices, { id: Date.now(), ...newDevice }]);
    setNewDevice({
      device_id: "",
      user_id: "",
      name: "",
      kWh: 0,
      time: "",
      address: "",
      status: "Off",
      date: "",
    });
  };

  const handleDelete = (id) => {
    setDevices(devices.filter((d) => d.id !== id));
  };

  return (
    <div className="device-container mb-6">
      <h2 className="text-xl font-bold mb-2">Quản lý thiết bị</h2>

      <div className="flex gap-2 mb-4">
        <input
          placeholder="Tên thiết bị"
          value={newDevice.name}
          onChange={(e) => setNewDevice({ ...newDevice, name: e.target.value })}
        />
        <input
          placeholder="kWh"
          type="number"
          value={newDevice.kWh}
          onChange={(e) => setNewDevice({ ...newDevice, kWh: e.target.value })}
        />
        <input
          placeholder="Status"
          value={newDevice.status}
          onChange={(e) =>
            setNewDevice({ ...newDevice, status: e.target.value })
          }
        />
        <button onClick={handleAdd}>Thêm</button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {devices.map((d) => (
          <div key={d.id} className="device-card">
            <h3>{d.name}</h3>
            <p>kWh: {d.kWh}</p>
            <p>Status: {d.status}</p>
            <p>Thời gian: {d.time}</p>
            <p>Địa chỉ: {d.address}</p>
            <button
              onClick={() => handleDelete(d.id)}
              style={{ color: "red", marginTop: "0.5rem" }}
            >
              Xóa
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
