import React from "react";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

function Dashboard({ devices = [] }) {
  // Đếm số lượng thiết bị theo phòng
  const addressCount = {};
  devices.forEach((d) => {
    if (!addressCount[d.ADDRESS]) addressCount[d.ADDRESS] = 0;
    addressCount[d.ADDRESS]++;
  });

  const labels = Object.keys(addressCount);
  const values = Object.values(addressCount);

  const chartData = {
    labels,
    datasets: [
      {
        label: "Số lượng thiết bị",
        data: values,
        backgroundColor: "#4caf50",
        borderColor: "#388e3c",
        borderWidth: 1,
      },
    ],
  };

  const pieData = {
    labels,
    datasets: [
      {
        data: values,
        backgroundColor: labels.map(
          () => "#" + Math.floor(Math.random() * 16777215).toString(16)
        ),
        borderColor: "#fff",
        borderWidth: 1,
      },
    ],
  };

  const options = {
    maintainAspectRatio: false,
    responsive: true,
    plugins: {
      tooltip: {
        callbacks: {
          label: function (context) {
            const label = labels[context.dataIndex];
            const value = values[context.dataIndex];
            return `${label}: ${value} thiết bị`;
          },
        },
      },
      legend: { position: "top" },
    },
  };

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ cột</h3>
        <Bar data={chartData} options={options} />
      </div>

      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ đường</h3>
        <Line data={chartData} options={options} />
      </div>

      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ tròn</h3>
        <Pie data={pieData} options={options} />
      </div>

      <div
        style={{
          flex: "1 1 100%",
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
        }}
      >
        <p>
          <strong>Số thiết bị:</strong> {devices.length}
        </p>
      </div>
    </div>
  );
}

export default Dashboard;
