import React, { useEffect } from "react";

function Device({ user, devices, setDevices }) {
  useEffect(() => {
    if (!user) return;

    const fetchDevices = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/devices-with-power/${user.USER_ID}`
        );
        const data = await res.json();

        setDevices(
          data.map((d) => ({
            ...d,
            color: "#" + Math.floor(Math.random() * 16777215).toString(16),
          }))
        );
      } catch (err) {
        console.error(err);
      }
    };

    fetchDevices();
  }, [user, setDevices]);

  return (
    <div style={{ marginTop: "20px" }}>
      <h3>Danh sách thiết bị</h3>
      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "16px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>kWh: {d.kWh != null ? d.kWh : 0}</p>
            <p>
              Time: {d.CREATE_AT ? new Date(d.CREATE_AT).toLocaleString() : "-"}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
