import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
const [user, setUser] = useState(null);
const [activeMenu, setActiveMenu] = useState("Dashboard");
const [devices, setDevices] = useState([]);

// Load devices khi login
useEffect(() => {
if (!user) return;
const fetchDevices = async () => {
try {
const res = await fetch(`http://localhost:5000/api/devices?user_id=${user.USER_ID}`);
const data = await res.json();
const devicesWithColor = data.map((d) => ({
...d,
kWh: d.kWh || 0,
date: d.date || new Date().toISOString().slice(0, 10),
color: "#" + Math.floor(Math.random() * 16777215).toString(16),
}));
setDevices(devicesWithColor);
} catch (err) {
console.error("Device load error:", err);
}
};
fetchDevices();
}, [user]);

const handleLogout = () => {
setUser(null);
setActiveMenu("Dashboard");
setDevices([]);
};

if (!user) return <Login onLogin={setUser} />;

let content;
switch (activeMenu) {
case "Dashboard":
content = <Dashboard devices={devices} />;
break;
case "Device":
content = <Device user={user} devices={devices} setDevices={setDevices} />;
break;
case "Setting":
content = <Setting user={user} />;
break;
default:
content = <Dashboard devices={devices} />;
}

return ( <div className="app-container"> <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} /> <div className="main-content"> <Header user={user} onLogout={handleLogout} />
{content} </div> </div>
);
}

export default App;
