import React from "react";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  ArcElement,
  Tooltip,
  Legend
);

function Dashboard({ devices = [] }) {
  const labels = devices.map((d) => d.DEVICE_NAME);
  const data = {
    labels,
    datasets: [
      {
        label: "Thiết bị",
        data: devices.map(() => 1),
        backgroundColor: devices.map((d) => d.color || "#888"),
        borderColor: devices.map((d) => d.color || "#888"),
        borderWidth: 1,
      },
    ],
  };

  const options = { responsive: true, maintainAspectRatio: false };

  return (
    <div style={{ display: "flex", flexWrap: "wrap", gap: 20 }}>
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ cột</h3>
        <Bar data={data} options={options} />
      </div>
      <div style={{ flex: "1 1 300px", height: 300 }}>
        <h3>Biểu đồ tròn</h3>
        <Pie data={data} options={options} />
      </div>
      <div
        style={{
          flex: "1 1 100%",
          padding: 16,
          border: "1px solid #ccc",
          borderRadius: 8,
        }}
      >
        <p>
          <strong>Số thiết bị:</strong> {devices.length}
        </p>
      </div>
    </div>
  );
}

export default Dashboard;
