import React, { useState, useEffect } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, devices, setDevices }) {
const [form, setForm] = useState({
DEVICE_NAME: "",
ADDRESS: addresses[0],
STATUS: "On",
});

// Lấy danh sách device từ backend khi component mount
useEffect(() => {
const fetchDevices = async () => {
try {
const res = await axios.get("[http://localhost:5000/api/devices](http://localhost:5000/api/devices)");
const devicesWithColor = res.data.map((d) => ({
...d,
color: "#" + Math.floor(Math.random() * 16777215).toString(16),
}));
setDevices(devicesWithColor);
} catch (err) {
console.error("Lỗi fetch devices:", err);
}
};

