import React, { useState } from "react";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, devices, addDevice }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  const handleAdd = () => {
    if (!form.DEVICE_NAME) return alert("Nhập tên thiết bị");

    addDevice({
      DEVICE_NAME: form.DEVICE_NAME,
      ADDRESS: form.ADDRESS,
      STATUS: form.STATUS,
    });

    setForm({ DEVICE_NAME: "", ADDRESS: addresses[0], STATUS: "On" });
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      <div style={{ padding: "16px", border: "1px solid #ccc", borderRadius: "8px", maxWidth: "400px" }}>
        <h4>Thêm thiết bị</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd} style={{ width: "100%" }}>
          Lưu thiết bị
        </button>
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              padding: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
            <p>POWER: {d.kWh} W</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
