import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Login from "./components/Login";
import "./App.css";

function App() {
const [user, setUser] = useState(null);
const [activeMenu, setActiveMenu] = useState("Dashboard");
const [devices, setDevices] = useState([]);

// Logout
const handleLogout = () => {
setUser(null);
setActiveMenu("Dashboard");
setDevices([]);
};

// Render content theo menu
let content;
switch (activeMenu) {
case "Dashboard":
content = <Dashboard devices={Array.isArray(devices) ? devices : []} />;
break;
case "Device":
content = (
<Device
user={user}
devices={Array.isArray(devices) ? devices : []}
setDevices={setDevices}
/>
);
break;
default:
content = <Dashboard devices={Array.isArray(devices) ? devices : []} />;
}

if (!user) {
return <Login onLogin={setUser} />;
}

return (
<div className="app-container" style={{ display: "flex", height: "100vh" }}> <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />
<div className="main-content" style={{ flex: 1, overflowY: "auto", padding: "16px" }}> <Header user={user} onLogout={handleLogout} />
{content} </div> </div>
);
}

export default App;
