import React, { useState, useEffect } from "react";
import axios from "axios";
import DeviceCard from "./DeviceCard";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user }) {
  const [devices, setDevices] = useState([]);
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    kWh: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  // Load devices của user
  const fetchDevices = async () => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/devices/user/${user.USER_ID}`
      );
      setDevices(res.data || []);
    } catch (err) {
      console.error("Error fetching devices:", err);
    }
  };

  useEffect(() => {
    if (user) fetchDevices();
  }, [user]);

  // Thêm device mới
  const handleAdd = async () => {
    if (!form.DEVICE_NAME || !form.kWh) {
      return alert("Nhập đủ Tên thiết bị và kWh");
    }

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        USER_ID: user.USER_ID,
        DEVICE_NAME: form.DEVICE_NAME,
        ADDRESS: form.ADDRESS,
        STATUS: form.STATUS,
        kWh: Number(form.kWh),
      });

      setDevices((prev) => [...prev, res.data]);
      setForm({
        DEVICE_NAME: "",
        kWh: "",
        ADDRESS: addresses[0],
        STATUS: "On",
      });
    } catch (err) {
      console.error("Error adding device:", err);
      alert("Thêm thiết bị thất bại!");
    }
  };

  // Xóa device
  const handleDelete = async (deviceId) => {
    try {
      await axios.delete(`http://localhost:5000/api/devices/${deviceId}`);
      setDevices(devices.filter((d) => d.DEVICE_ID !== deviceId));
    } catch (err) {
      console.error("Error deleting device:", err);
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      {/* Form thêm device */}
      <div
        style={{
          padding: 16,
          border: "1px solid #ccc",
          borderRadius: 8,
          maxWidth: 400,
        }}
      >
        <h4>Thêm thiết bị mới</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: 8 }}
        />
        <input
          type="number"
          placeholder="kWh"
          value={form.kWh}
          onChange={(e) => setForm({ ...form, kWh: e.target.value })}
          style={{ width: "100%", marginBottom: 8 }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: 8 }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: 8 }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd} style={{ width: "100%" }}>
          Thêm thiết bị
        </button>
      </div>

      {/* Device cards */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((device) => (
          <DeviceCard
            key={device.DEVICE_ID}
            device={device}
            onDelete={() => handleDelete(device.DEVICE_ID)}
          />
        ))}
      </div>
    </div>
  );
}

export default Device;
