/* App.js */
import React, { useState, useEffect } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Device from "./components/Device";
import Setting from "./components/Setting";
import Login from "./components/Login";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [activeMenu, setActiveMenu] = useState("Dashboard");

  const [devices, setDevices] = useState([]);

  // Load device theo USER_ID sau khi login
  useEffect(() => {
    if (!user) return;

    fetch(`http://localhost:3001/api/device/${user.USER_ID}`)
      .then((res) => res.json())
      .then((data) => {
        console.log("DEVICE DATA:", data);
        setDevices(data);
      })
      .catch((err) => console.error("Device load error:", err));
  }, [user]);

  // Hàm logout
  const handleLogout = () => {
    setUser(null);
    setActiveMenu("Dashboard");
    setDevices([]);
  };

  // Nếu chưa login → hiện Login
  if (!user) {
    return <Login onLogin={setUser} />;
  }

  // Render nội dung chính
  let content;
  switch (activeMenu) {
    case "Dashboard":
      content = <Dashboard devices={devices} />;
      break;
    case "Device":
      content = (
        <Device user={user} devices={devices} setDevices={setDevices} />
      );
      break;
    case "Setting":
      content = <Setting user={user} />;
      break;
    default:
      content = <Dashboard devices={devices} />;
  }

  return (
    <div className="app-container">
      <Sidebar activeMenu={activeMenu} setActiveMenu={setActiveMenu} />

      <div className="main-content">
        <Header user={user} onLogout={handleLogout} />
        {content}
      </div>
    </div>
  );
}

export default App;
