const chartLabels = devices.map((d) => d.DEVICE_NAME);

const chartData = {
  labels: chartLabels,
  datasets: [
    {
      label: "kWh",
      data: devices.map((d) => Number(d.kWh || 0)),
      borderWidth: 2,
      backgroundColor: devices.map((d) => d.color || "#888888"),
      borderColor: devices.map((d) => d.color || "#888888"),
    },
  ],
};

const pieData = {
  labels: chartLabels,
  datasets: [
    {
      data: devices.map((d) => Number(d.kWh || 0)),
      backgroundColor: devices.map((d) => d.color || "#888888"),
      borderColor: devices.map((d) => d.color || "#888888"),
      borderWidth: 1,
    },
  ],
};

const options = {
  maintainAspectRatio: false,
  plugins: {
    tooltip: {
      callbacks: {
        label: function (context) {
          const d = devices[context.dataIndex];
          return `${d.DEVICE_NAME}: ${d.kWh} kWh | ${d.DEVICE_ID} | ${d.USER_ID} | ${d.ADDRESS}`;
        },
      },
    },
  },
};
