import React, { useState } from "react";
import { Bar, Line, Pie } from "react-chartjs-2";

function Dashboard({ devices }) {
  // ========== FILTER =============
  const [startDate, setStartDate] = useState("2025-01-01");
  const [endDate, setEndDate] = useState("2025-12-31");

  const filtered = devices.filter((d) => {
    const day = new Date(d.date);
    return day >= new Date(startDate) && day <= new Date(endDate);
  });

  // ========== DATA ===============
  const totalKwh = filtered.reduce((sum, d) => sum + Number(d.kWh), 0);

  const chartData = {
    labels: filtered.map((d) => d.name),
    datasets: [
      {
        label: "kWh",
        data: filtered.map((d) => d.kWh),
        borderWidth: 2,
      },
    ],
  };

  const pieData = {
    labels: filtered.map((d) => d.name),
    datasets: [
      {
        data: filtered.map((d) => d.kWh),
      },
    ],
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6">Dashboard</h2>

      {/* ======= Lọc ngày ======= */}
      <div className="flex gap-4 mb-8">
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          className="p-2 border rounded"
        />
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          className="p-2 border rounded"
        />
      </div>

      {/* ======= Thống kê ======= */}
      <div className="grid grid-cols-2 gap-6 mb-10">
        <div className="bg-white p-5 rounded-xl shadow">
          <p className="text-gray-600">Tổng năng lượng</p>
          <p className="text-3xl font-bold">{totalKwh} kWh</p>
        </div>

        <div className="bg-white p-5 rounded-xl shadow">
          <p className="text-gray-600">Số thiết bị</p>
          <p className="text-3xl font-bold">{filtered.length}</p>
        </div>
      </div>

      {/* ======== Charts ========== */}
      <div className="grid grid-cols-3 gap-5">
        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="font-semibold">Biểu đồ cột</h3>
          <Bar data={chartData} />
        </div>

        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="font-semibold">Biểu đồ đường</h3>
          <Line data={chartData} />
        </div>

        <div className="bg-white p-4 rounded-xl shadow">
          <h3 className="font-semibold">Pie Chart</h3>
          <Pie data={pieData} />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
