import React, { useState } from "react";
import axios from "axios";

const addresses = ["Phòng khách", "Phòng ngủ", "Nhà bếp", "Phòng làm việc"];

function Device({ user, devices, setDevices }) {
  const [form, setForm] = useState({
    DEVICE_NAME: "",
    kWh: "",
    ADDRESS: addresses[0],
    STATUS: "On",
  });

  const handleAdd = async () => {
    if (!form.DEVICE_NAME || !form.kWh) return alert("Nhập đầy đủ thông tin");

    try {
      const res = await axios.post("http://localhost:5000/api/devices", {
        ...form,
        USER_ID: user.USER_ID,
        kWh: Number(form.kWh),
      });

      const newDevice = {
        ...res.data,
        color: "#" + Math.floor(Math.random() * 16777215).toString(16),
      };
      setDevices([...devices, newDevice]);

      setForm({
        DEVICE_NAME: "",
        kWh: "",
        ADDRESS: addresses[0],
        STATUS: "On",
      });
    } catch (err) {
      console.error(err);
      alert("Lưu thiết bị thất bại!");
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", gap: "16px" }}>
      <div
        style={{
          padding: "16px",
          border: "1px solid #ccc",
          borderRadius: "8px",
          maxWidth: "400px",
        }}
      >
        <h4>Thêm thiết bị</h4>
        <input
          type="text"
          placeholder="Tên thiết bị"
          value={form.DEVICE_NAME}
          onChange={(e) => setForm({ ...form, DEVICE_NAME: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        />
        <input
          type="number"
          placeholder="kWh"
          value={form.kWh}
          onChange={(e) => setForm({ ...form, kWh: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        />
        <select
          value={form.ADDRESS}
          onChange={(e) => setForm({ ...form, ADDRESS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          {addresses.map((a) => (
            <option key={a} value={a}>
              {a}
            </option>
          ))}
        </select>
        <select
          value={form.STATUS}
          onChange={(e) => setForm({ ...form, STATUS: e.target.value })}
          style={{ width: "100%", marginBottom: "8px" }}
        >
          <option>On</option>
          <option>Off</option>
        </select>
        <button onClick={handleAdd} style={{ width: "100%" }}>
          Lưu thiết bị
        </button>
      </div>

      <div style={{ display: "flex", flexWrap: "wrap", gap: "16px" }}>
        {devices.map((d) => (
          <div
            key={d.DEVICE_ID}
            style={{
              padding: "16px",
              border: "1px solid #ccc",
              borderRadius: "8px",
              minWidth: "200px",
              borderLeft: `4px solid ${d.color}`,
            }}
          >
            <h4>{d.DEVICE_NAME}</h4>
            <p>kWh: {d.kWh}</p>
            <p>Địa chỉ: {d.ADDRESS}</p>
            <p>Status: {d.STATUS}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Device;
